...

  go func() {
      select {
      case <-ctx.Done():
          // Context timeout or cancellation.
          resultC <- StateStatus{
              exitCode: -1,
              err:      ctx.Err(),
          }
          return
      case <-waitStop:
      case <-waitRemove:
      }

      s.Lock() // <-- Time is spent waiting here
      result := StateStatus{
          exitCode: s.ExitCode(),
          err:      s.Err(),
      }
      s.Unlock()

      resultC <- result
  }()

  return resultC

